        documentUrl: "https://dip21.bundestag.de/dip21/btp/12/12012.pdf",
        positionShort: "Secretary of State",
        positionLong: "parl. staatssekretär beim bundesminister der finanzen",
        date: "1991-02-28T00:00:00.000Z",
