        politicianId: "11000127",
        speechContent:
          "Das Wort hat nunmehr der Abgeordneten Schwanitz, SPD-Fraktion.\n",
        factionId: "-1",
        documentUrl: "https://dip21.bundestag.de/dip21/btp/12/12012.pdf",
